<?php/** * Created By IntelliJ IDEA * @author Roy * @date: 2022/4/27 * @time: 14:10 */namespace console\controllers;use yii\console\Controller;class HelloController extends Controller{    /**     * 为命令行定义选项     * @var $rev     */    public $rev;    /**     * 重写 options 方法，给命令添加选项     * @param string $actionID     * @return string[]     */    public function options($actionID)    {        return ['rev'];    }    /**     * 重写 optionAliases 方法，给选项设置别名     * @return string[]     */    public function optionAliases()    {        return ['r' => 'rev'];    }    /**     * 使用选项的命令行测试方法     * 注意：     *  不使用别名简写的方式选项需要两个 -     *      php yii hello/option --rev=1     *  使用别名简写的方式选项只有一个 -     *      php yii hello/option -r=1     *     */    public function actionOption()    {        if ($this->rev == 1) {            echo strrev('hello world') . "\r\n";        } else {            echo "hello world\r\n";        }    }    /**     * index 是默认动作，     * 命令行执行 php yii hello/index 或 php yii hello 都可以执行这个方法     */    public function actionIndex()    {        echo "hello world \r\n";    }    /**     * 带参的命令，参数可以传递多个，在命令行中的内容依次传递给命令行方法     * php yii hello/who zrh 18     * @param $name     * @param $age     * @param $arrParam     */    public function actionWho($name, $age, $arrParam)    {        echo "hello {$name}，age {$age} \r\n";        var_dump($arrParam);    }}