<?php/** * Created By IntelliJ IDEA * @author Roy * @date: 2022/4/27 * @time: 11:03 */namespace frontend\components;use yii\base\Widget;use yii\helpers\Html;class RctReplyWidget extends Widget{    public $recentComments;    public function init()    {        parent::init();    }    public function run()    {        $commentString = '';        foreach ($this->recentComments as $comment) {            $commentString .= '<div class="post">'.                '<div class="title">'.                '<p style="color:#777;font-style:italic;">'.                nl2br($comment->content).'</p>'.                '<p style="font-size:8px;color:blue">《<a href="'.$comment->post->url.'">'.Html::encode($comment->post->title).'</a>》</p>'.                '<hr></div></div>';        }        return $commentString;    }}